htmx.defineExtension("refresh-href",{onEvent:function(e,t){let r="_refresh_href_";if(e==="htmx:beforeRequest"){if(t.detail.requestConfig.triggeringEvent===r){t.detail.etc.handler(t.detail.pathInfo.anchor,t.detail.pathInfo.finalRequestPath);return false}}else if(e==="htmx:afterProcessNode"){let i=t.detail.elt;if(i.tagName==="A"&&i.getAttribute("hx-get")){let e=htmx.closest(i,"[refresh-href]");let t=(e&&e.getAttribute("refresh-href")||"init,mouseover").split(",");t.forEach(function(e){let t=function(){htmx.ajax("GET",i.getAttribute("hx-get"),{source:i,event:r,handler:function(e,t){let r=t+(e&&!t.includes("#")?"#"+e:"");i.setAttribute("href",r)}})};if(e==="init"){t()}else{i.addEventListener(e,t)}})}}}});